input = new Scanner(System.in)
N = input.nextInt()
Stack stack = new Stack()
input.nextLine()
for (;;) {
	s = input.nextLine()
	if (s == 'end') break
	if (s == 'if') stack.push(s)
	else if (s == 'else') stack.push(s)
	else if (s == 'endif') {
		def ll = new ArrayList()
		ll.add('endif')
		for (;;) {
			s1 = stack.pop()
			ll.add(s1)
			if (s1 == 'if') break
		}
		count = getCount(ll)
		stack.push(count)
	}
	else {
		if (s.isLong()) stack.push(s)
	}
}

def long n = 1
for (String s : stack) {
	n *= Integer.parseInt(s)
}

println n

def getCount(list) {
	def n1 = 1
	def n2 = 1
	def status = 0
	list.each {
		String s = it
		if (s == 'endif') status++
		else if (s == 'else') status++
		else if (s == 'if') status = 0
		else {
			if (status == 1) {
				if (s.isLong()) {
					n1 *= s.toLong()
				}
			} else if (status == 2) {
				if (s.isLong()) {
					n2 *= s.toLong()
				}
			}
		}
	}
	return n1 + n2
}
