#include <bits/stdc++.h>
using namespace std;
int main(){
  int N;
  string s;
  cin>>N>>s;
  vector<string> S(N-1);
  for(int i=0;i<N-1;i++)cin>>S[i];
  sort(S.begin(),S.end());
  while(S.size()>0){
    for(int i=0;i<S.size();i++){
      int cnt=0;
      for(int j=0;j<S[i].size();j++)if(s[j]!=S[i][j])cnt++;
      if(cnt==1){
        s=S[i];
        S.erase(S.begin()+i);
        break;}}}
  cout<<s;}
