def calculate_grade(score):
    if score >= 90.00:
        return 'A'
    elif score >= 80.00:
        return 'B'
    elif score >= 70.00:
        return 'C'
    elif score >= 60.00:
        return 'D'
    else:
        return 'F'

n = int(input())
for _ in range(n):
    data = input().split()
    name = data[0]
    scores = list(map(int, data[1:]))
    average = sum(scores) / len(scores)
    grade = calculate_grade(average)
    print(f"{name} {average:.2f} {grade}")
