n = int(input())

def denombrement_molkky(initial,final):
    difference = final - initial + 1
    scoreMemoire = [1] * difference

    for ecart in range(1,difference):
        possibilite = 0
        for score in range(1,13):
            double = 1
            if score > 1:
                double = 2

            if score <= ecart:
                possibilite += scoreMemoire[ecart - score] * double

        scoreMemoire[ecart] = possibilite

    return scoreMemoire[-1]


print(denombrement_molkky(n,50))
