def phenomena(n):
    n=str(n)
    n=n.replace('8','')
    index=0
    while index<len(n):
        if n[index]=='7':
            low=index-1
            while low>=0 and n[low]=='9':
                low-=1
            high=index+1
            while high<len(n) and n[high]=='9':
                high+=1
            n=n[:low+1]+'7'+n[high:]
            index=low+1
        index+=1
    if len(n)==0:
        n='Nothing!'
    return n

if __name__=='__main__':
    sent_num=int(input())
    received_num=phenomena(sent_num)
    print(received_num)
