#include <iostream>
#include <sstream>
#include <bitset>
#include <string>

using namespace std;

string applyCoding(const string& input) {
    stringstream ss(input);
    string output;

    int n;
    while (ss >> n) {
        if (n >= 0 && n <= 255) {
            bitset<8> binary(n);
            bitset<8> codedBinary;

            codedBinary[0] = binary[7];
            codedBinary[7] = binary[0];
            for (int i = 1; i < 7; i++) {
                codedBinary[i] = binary[i];
            }

            int codedNumber = codedBinary.to_ulong();
            output += to_string(codedNumber) + " ";
        }
    }

    return output.substr(0, output.size() - 1);
}


int main() {
    string s;
    getline(cin, s);

    string output = applyCoding(s);

    cout <<  output << endl;

    return 0;
}
