using System; using System.Linq; using System.IO; using System.Text; using System.Collections; using System.Collections.Generic;	

public class Program
{
	public static List<string> ClueList = new List<string>();
	public static string bl = " ";

	public static void Main()
	{
        int min = 1;
		int maxNum = int.Parse(Console.ReadLine());
        int max = maxNum;
		int numOfAttempts = int.Parse(Console.ReadLine());

		char[] delimiterChars = { ':', ' ' };

		for (int i = 0 ; i < numOfAttempts ; i++)
        {
            string item = Console.ReadLine();	
			string[] Ray = item.Split(delimiterChars);
			string dir = Ray[3];
			int num = int.Parse(Ray[1]);
			if (dir == "higher")
			{
				min = num + 1;
			}
			if (dir == "lower")
			{
				max = num - 1;
			}
			string str = PrintPossibles(min,max);
			Console.WriteLine(str);
		}
	}

	public static string PrintPossibles(int aa, int zz)
	{
		string output = "";
		for (int i = aa ; i <= zz ; i++)
		{
			output += i + bl;
		}
		output = output.Trim();
		return output;
	}
}

