using System;
using System.Linq;
using System.IO;
using System.Text;
using System.Collections;
using System.Collections.Generic;
using System.Drawing;
/**
 * Auto-generated code below aims at helping you parse
 * the standard input according to the problem statement.
 **/
class Solution
{

    private static Dictionary<char, Point> LocateAsteroids(string[] sky, int w, int h)
    {
        var output = new Dictionary<char, Point>();

        for (int r = 0; r < h; r++)
        {
            for (int c = 0; c < w; c++)
            {
                if (char.IsLetter(sky[r][c]))
                {
                    output[sky[r][c]] = new Point(c, r);
                }
            }
        }
        return output;
    }

    static void Main(string[] args)
    {
        string[] inputs = Console.ReadLine().Split(' ');
        int W = int.Parse(inputs[0]);
        int H = int.Parse(inputs[1]);
        int T1 = int.Parse(inputs[2]);
        int T2 = int.Parse(inputs[3]);
        int T3 = int.Parse(inputs[4]);

        var sky1 = new string[H];
        var sky2 = new string[H];

        for (int i = 0; i < H; i++)
        {
            var row = Console.ReadLine().Split();
            sky1[i] = row[0];
            sky2[i] = row[1];
        }

        var asteroidsA = LocateAsteroids(sky1, W, H);
        var asteroidsB = LocateAsteroids(sky2, W, H);

        double deltaT1 = T2 - T1;
        double deltaT2 = T3 - T1;


        var output = new char[H][];

        for (int i = 0; i < H; i++)
        {
            output[i] = new string('.', W).ToArray();
        }


        foreach (var asteroid in asteroidsB.OrderByDescending(x => x.Key))
        {
            var o = asteroidsA[asteroid.Key];
            var p = asteroid.Value;
            var xSpeed = (p.X - o.X) / deltaT1;
            var ySpeed = (p.Y - o.Y) / deltaT1;

            var X = (int)Math.Floor(o.X + xSpeed * deltaT2);
            var Y = (int)Math.Floor(o.Y + ySpeed * deltaT2);

            if (X >= 0 && X < W && Y >= 0 && Y < H)
            {
                output[Y][X] = asteroid.Key;
            }

        }

        foreach (var r in output)
            Console.WriteLine(r);


    }
}
