import java.io.*;
import java.util.*;

public class Solution {

	static boolean DEBUG = false;

	static final List<String> VALID_SYLLOGIMS = Arrays.asList( //
			"AAA-1", "EAE-1", "AII-1", "EIO-1", "AAI-1", "EAO-1", //
			"EAE-2", "AEE-2", "EIO-2", "AOO-2", "EAO-2", "AEO-2", //
			"AII-3", "IAI-3", "EIO-3", "OAO-3", "EAO-3", "AAI-3", //
			"AEE-4", "IAI-4", "EIO-4", "AEO-4", "EAO-4", "AAI-4" //
	);

	static class Premise {
		final char type;
		final String subject;
		final String predicate;

		Premise(String[] words) {
			subject = words[1];
			predicate = words[3].equals("not") ? words[4] : words[3];
			if ("all".equals(words[0])) {
				if (words[3].equals("not")) {
					type = 'E';
				} else {
					type = 'A';
				}
			} else if ("no".equals(words[0])) {
				if (words[3].equals("not")) {
					type = 'A';
				} else {
					type = 'E';
				}
			} else {
				if ("not".equals(words[3])) {
					type = 'O';
				} else {
					type = 'I';
				}
			}

		}

	}

	static class Conclusion extends Premise {
		Conclusion(String[] words) {
			super(words);
		}
	}

	static class Syllogism {
		final Premise majorPremise;
		final Premise minorPremise;
		final Conclusion conclusion;

		Syllogism(String line) {
			String[] words = line.split(" ");
			List<String> majorPremiseWords = new ArrayList<>();
			List<String> minorPremiseWords = new ArrayList<>();
			List<String> conclusionWords = new ArrayList<>();
			List<String> current = majorPremiseWords;
			for (String word : words) {
				if ("and".equals(word)) {
					current = minorPremiseWords;
				} else if ("therefore".equals(word)) {
					current = conclusionWords;
				} else {
					current.add(word);
				}
			}
			majorPremise = new Premise(majorPremiseWords.toArray(new String[0]));
			minorPremise = new Premise(minorPremiseWords.toArray(new String[0]));
			conclusion = new Conclusion(conclusionWords.toArray(new String[0]));
		}

		boolean isValid() {
			// identify the major, minor and middle terms
			String majorTerm = conclusion.predicate;
			String minorTerm = conclusion.subject;
			String middleTerm = majorPremise.subject.equals(majorTerm) || majorPremise.subject.equals(minorTerm) ? majorPremise.predicate : majorPremise.subject;
            debug("major: %s, minor : %s, middle : %s ", majorTerm, minorTerm, middleTerm);
			// find out the figure of the syllogism
			int figure = 0;
			if (middleTerm.equals(majorPremise.subject) && middleTerm.equals(minorPremise.predicate) && majorTerm.equals(majorPremise.predicate) && minorTerm.equals(minorPremise.subject) ) {
				figure = 1;
			} else if (middleTerm.equals(majorPremise.predicate) && middleTerm.equals(minorPremise.predicate) && majorTerm.equals(majorPremise.subject) && minorTerm.equals(minorPremise.subject) ) {
				figure = 2;
			} else if (middleTerm.equals(majorPremise.subject) && middleTerm.equals(minorPremise.subject) && majorTerm.equals(majorPremise.predicate) && minorTerm.equals(minorPremise.predicate)  ) {
				figure = 3;
			} else if (middleTerm.equals(majorPremise.predicate) && middleTerm.equals(minorPremise.subject) && majorTerm.equals(majorPremise.subject) && minorTerm.equals(minorPremise.predicate)  ) {
				figure = 4;
			} else {
			    figure = 0;
			}
			String syllogismMoodAndFigure = majorPremise.type + "" + minorPremise.type + "" + conclusion.type + "-" + figure;

			debug(" -> %s -> ", syllogismMoodAndFigure);

			return VALID_SYLLOGIMS.contains(syllogismMoodAndFigure);
		}
	}

	static void run(InputStream inputStream) {
		try (Scanner in = new Scanner(inputStream)) {
			int nbSyllogyms = in.nextInt();
			in.nextLine();
			for (int i = 0; i < nbSyllogyms; i++) {
				String line = in.nextLine();
				debug("%s\n", line);
				Syllogism syllogism = new Syllogism(line);
				System.out.println(syllogism.isValid() ? "VALID" : "INVALID");
			}
		} catch (Exception e) {
			e.printStackTrace();
		}
	}

	public static void main(String[] args) {
		run(System.in);
	}

	private static void debug(String msg, Object... args) {
		if (DEBUG) {
			System.err.print(String.format(msg, args));
		}
	}

}
