const N = parseInt(readline());
let list = readline().split(' ');
const P = parseInt(readline());

const bubbleSort = (list) => {
	const L = [...list];
    for(let i = 1; i < L.length; i++) {
        if (Number(L[i]) < Number(L[i-1])) {
            let temp = L[i-1];
            L[i-1] = L[i];
            L[i] = temp
        }
    }
	return L;
}

for (let i = 0; i < P; i++) {
    list = bubbleSort(list);
}

console.log(list.join(' '));
