#include <stdlib.h>
#include <stdio.h>
#include <string.h>
#include <stdbool.h>
#include <math.h>

int main()
{
    int weight;
    scanf("%d", &weight);
    int feet, inches;
    scanf("%d%d", &feet, &inches);
    int heightInInches = feet * 12 + inches;
    float BMI = 703 * (float)weight / (((float)heightInInches) * ((float)heightInInches));
    float BMIRounded = roundf(BMI * 10) / 10;
    printf("%.1f\n", BMIRounded);

    if(BMIRounded < 18.5)
    {
        printf("Underweight");
    }
    else if(BMIRounded < 25.0)
    {
        printf("Normal");
    }
    else if(BMIRounded < 30.0)
    {
        printf("Overweight");
    }
    else
    {
        printf("Obese");
    }

    return 0;
}
