import java.util.*;
import java.io.*;
import java.math.*;
import java.util.stream.Collectors;

/**
 * Auto-generated code below aims at helping you parse
 * the standard input according to the problem statement.
 **/
class Solution {

    public static void main(String args[]) {
        Scanner in = new Scanner(System.in);
        List<Integer> crossingPoints =
                Arrays.stream(in.nextLine().split(" "))
                        .map(i -> Integer.parseInt(i))
                        .collect(Collectors.toList());
        List<String> chromosome1 = Arrays.asList(in.nextLine().split(" "));
        List<String> chromosome2 = Arrays.asList(in.nextLine().split(" "));

        List<String> childChromosome1 = new ArrayList<>();
        List<String> childChromosome2 = new ArrayList<>();

        int pointBefore = 0;
        crossingPoints.add(chromosome1.size());
        int current = 1;
        for (Integer crossingPoint : crossingPoints) {
            if (current == 1) {
                childChromosome1.addAll(chromosome1.subList(pointBefore, crossingPoint));
                childChromosome2.addAll(chromosome2.subList(pointBefore, crossingPoint));
                current = 2;
            } else {
                childChromosome2.addAll(chromosome1.subList(pointBefore, crossingPoint));
                childChromosome1.addAll(chromosome2.subList(pointBefore, crossingPoint));
                current = 1;
            }
            pointBefore = crossingPoint;
        }

        System.out.println(String.join(" ", childChromosome1));
        System.out.println(String.join(" ", childChromosome2));
    }
}
