input = new Scanner(System.in)
N = input.nextInt()
input.nextLine()
for (i = 0; i < N; ++i) {
	str = input.nextLine()
	def list = []
	for (char c : str.toCharArray()) {
		added = false;
		for (Stack sk : list) {
			if (c <= sk.peek()) {
				sk.add(c)
				added = true
				break
			}
		}
		if (!added) {
			Stack sk = new Stack()
			sk.add(c)
			list.add(sk)
		}
	}
	println list.size()
}
