ancestor_name = input()

n_of_persons = int(input())

mothers = {}

for line in range(n_of_persons):
    name, mother = input().split()
    mothers[name] = mother


def is_descendant(name):
    if ancestor_name == name:
        return False

    mother = mothers[name]

    return ancestor_name == mother or mother in mothers and is_descendant(mother)


descendants = sorted(name for name in mothers if is_descendant(name)) or ['Nobody']

print(*descendants, sep='\n')

