static void updateStaticstics(RoundInfo roundInfo){
int cardNb=BoardUtils.calculateCardNb(roundInfo.boardCard);
int action=roundInfo.action;
int type=ActionUtils.getType(action);
if(type==ActionUtils.NONE){
return;
}
int id=roundInfo.playerId;
PhaseStatistic phaseS=StatisticUtils.update(roundInfo.handNb,id,cardNb);
int amount=0;
int callAmount=calculateCallAmount(id);
if(type==ActionUtils.BET){
phaseS.bet=true;
amount=ActionUtils.getAmount(action);
}else if(type==ActionUtils.ALL_IN){
amount=stacks[id];
phaseS.allIn=true;
}else if(type==ActionUtils.CALL){
amount=callAmount;
}else if(type==ActionUtils.FOLD||type==ActionUtils.TIMEOUT){
phaseS.fold=true;
phaseS.foldChip=callAmount;
}
phaseS.chipAdded+=amount;
}
public static void doAsserts(){
Game game=Game.getInstance();
int sum=0;
for(int i=0;i<playerNb;i++){


sum+=stacks[i]+bets[i];
}

}
static void endTurn(){
calculateWinnings();
MessageUtils.clearWinner();
for(int i=0;i<playerNb;i++){
stacks[i]+=winnings[i];
if(winnings[i]>0){
MessageUtils.addWinner(i);
}
bets[i]=0;
folded[i]=false;
}
}
static void doAction(int id,int action){
int type=ActionUtils.getType(action);
if(type==ActionUtils.NONE){
return;
}
actionCount[id][type]++;
actionTotalCount[id]++;
if(type==ActionUtils.BET){
int amount=ActionUtils.getAmount(action);
doBet(id,amount);
}else if(type==ActionUtils.ALL_IN){
doBet(id,stacks[id]);
}else if(type==ActionUtils.CALL){
int callAmount=calculateCallAmount(id);

doBet(id,callAmount);
}else if(type==ActionUtils.FOLD){
folded[id]=true;
}else if(type==ActionUtils.TIMEOUT){
folded[id]=true;
removeTimeOutChips+=stacks[id];
stacks[id]=0;
}
}
public static int calculateCallAmount(int id){
int maxBet=Integer.MIN_VALUE;
for(int i=0;i<playerNb;i++){
maxBet=Math.max(maxBet,bets[i]);
}
if(isPreflop()&&maxBet<bigBlind){
maxBet=bigBlind;
}
int callAmount=maxBet-bets[id];
return callAmount;
}
public static boolean isPreflop(){
return CardUtils.isBurned(lastBoard[0]);
}
public static boolean isAlwaysAllIn(int id){
if(FOWUtils.isUseStatistics()&&!GameUtils.folded[id]){
for(int i=0;i<actionCount[id].length;i++){
if(i!=ActionUtils.ALL_IN&&actionCount[id][i]>0){
return false;
}
}
return true;
}
return false;
}
