calculateNextBBId();
for(int i=0;i<playerNb;i++){
folded[i]=stacks[i]<=0;
if(!eliminated[i]&&stacks[i]==0){
eliminated[i]=true;
}

}
initPosition();
for(int i=0;i<stacks.length;i++){
if(stacks[i]>0){
int bet=i==bbId?bigBlind:smallBlind;
bet=Math.min(stacks[i],bet);
if(i!=sbId&&stacks[i]<bigBlind){
bet=stacks[i];
}
doBet(i,bet);
}else{

}
}
resetRoundBets();
nextPlayerId=bbId;
