static void incrementLevel(){
if(currentHand%Constant.HAND_NB_BY_LEVEL==0){
level++;
smallBlind*=Constant.LEVEL_BLIND_MULTIPLIER;
bigBlind*=Constant.LEVEL_BLIND_MULTIPLIER;
}
}
static void calculateNextBBId(){
if(currentHand>1){
do{
bbId++;
bbId%=Constant.PLAYER_NB;
}while(stacks[bbId]==0);
}
}
static void doBet(int id,int amount){
stacks[id]-=amount;
bets[id]+=amount;
roundBets[id]+=amount;

}
static void calculateWinnings(){
for(int i=0;i<playerNb;i++){
winnings[i]=0;
betWinnings[i]=bets[i];
}
lastBoard=ActionRoundUtils.showDownInfos[currentHand].boardCards;
assertShowDownCards();
if(calculateNotFoldedNb()>1){
initPlayerBestHands();
}
resolveSidePot();
for(int i=0;i<playerNb;i++){
int remain=betWinnings[i];
winnings[i]+=remain;
}
}
static void assertShowDownCards(int i,int value){
int[] cards=ActionRoundUtils.showDownInfos[currentHand].playerCards;

}
static void assertShowDownCards(){
if(calculateNotFoldedNb()>1){
for(int i=0;i<playerNb;i++){
if(i!=Constant.PLAYER_ID){
if(eliminated[i]){
assertShowDownCards(i,CardUtils.ELIMINATED);
}else{
int[] cards=ActionRoundUtils.showDownInfos[currentHand].playerCards;
}
}
}
}else{
for(int i=0;i<playerNb;i++){
if(i!=Constant.PLAYER_ID&&eliminated[i]){
assertShowDownCards(i,eliminated[i]?CardUtils.ELIMINATED:CardUtils.BURNED);
}
}
}
}
public static int calculateNotFoldedNb(){
int nb=0;
for(int i=0;i<playerNb;i++){
if(!folded[i]){
nb++;
}
}
return nb;
}
public static int calculateNotFoldedNeverFoldNb(){
int nb=0;
for(int i=0;i<playerNb;i++){
if(!folded[i]&&isNeverFold(i)){
nb++;
}
}
return nb;
}
static void initPlayerBestHands(){
extractBoard();
int[] cards=ActionRoundUtils.showDownInfos[currentHand].playerCards;
for(int i=0;i<playerNb;i++){
int value=Integer.MIN_VALUE;
if(!folded[i]&&BoardUtils.calculateCardNb(lastBoard)==5){
tmpHand[0]=cards[2*i];
tmpHand[1]=cards[2*i+1];
value=CactusKevUtils.calculateBestPossibleScore(board,tmpHand);
}
bestPossibleHandValue[i]=value;
}
}
static void extractBoard(){
for(int i=0;i<5;i++){
board[i]=lastBoard[i];
}
}
static void resolveSidePot(){
int minBet=Integer.MAX_VALUE;
int maxBet=Integer.MIN_VALUE;
for(int i=0;i<playerNb;i++){
if(!folded[i]){
minBet=Math.min(minBet,betWinnings[i]);
maxBet=Math.max(maxBet,betWinnings[i]);
}
}
boolean allIn=minBet!=maxBet;
if(!allIn){
applyWinnings(minBet);
return;
}
applyWinnings(minBet);
resolveSidePot();
}
static void applyWinnings(int minBet){
int[] winnerIds=findWinner();
int potSplitTotal=0;
for(int i=0;i<playerNb;i++){
int tmp=Math.min(betWinnings[i],minBet);
potSplitTotal+=tmp;
betWinnings[i]-=tmp;
if(betWinnings[i]==0){
folded[i]=true;
}
}
int winnerNb=IntListUtils.size(winnerIds);
int potSplit=potSplitTotal/winnerNb;
int remaining=potSplitTotal%winnerNb;
for(int i=0;i<winnerNb;i++){
int playerId=IntListUtils.get(winnerIds,i);
winnings[playerId]+=potSplit;
}
int oddChipWinningPlayerId=dealerId+1;
while(true){
oddChipWinningPlayerId%=playerNb;
if(IntListUtils.contains(winnerIds,oddChipWinningPlayerId)){
break;
}
oddChipWinningPlayerId++;
}
winnings[oddChipWinningPlayerId]+=remaining;
}
static int[] findWinner(){
int bestScore=Integer.MIN_VALUE;
IntListUtils.clear(bestPlayerIds);
for(int i=0;i<playerNb;i++){
if(!folded[i]){
int score=bestPossibleHandValue[i];
if(score>bestScore){
bestScore=score;
IntListUtils.clear(bestPlayerIds);
IntListUtils.add(bestPlayerIds,i);
}else if(score==bestScore){
IntListUtils.add(bestPlayerIds,i);
}
}
}
return bestPlayerIds;
}
static void initPosition(){
int startIndex=bbId;
sbId=-1;
dealerId=-1;
int nb=0;
for(int i=0;i<playerNb;i++){
int playerIndex=(startIndex-1-i)%playerNb;
if(playerIndex<0){
playerIndex+=playerNb;
}
if(!folded[playerIndex]){
nb++;
if(sbId==-1){
sbId=playerIndex;
}else if(dealerId==-1){
dealerId=playerIndex;
}
}
}

if(nb==2){
dealerId=sbId;
}
nextPlayerId=bbId;
logger.error("initPosition",nb,"bbId",bbId,"dealerId",dealerId);

}
static void calculateNextPlayer(){
int idx=nextPlayerId+1;
nextPlayerId=-1;
if(!calculateNoMoreCanDoAction()){
for(int i=0;i<playerNb;i++){
int playerIndex=idx%playerNb;
if(!folded[playerIndex]&&stacks[playerIndex]>0){
nextPlayerId=playerIndex;
break;
}
idx++;
}
}
}
static boolean calculateNoMoreCanDoAction(){
int nb=0;
int max=0;
int id=-1;
for(int i=0;i<playerNb;i++){
if(!folded[i]&&stacks[i]>0){
nb++;
id=i;
}
max=Math.max(bets[i],max);
}
if(nb<=1){
return bets[id]>=max;
}
return false;
}
public static int getPot(){
int ret=0;
for(int i=0;i<playerNb;i++){
ret+=bets[i];
}
return ret;
}
public static int getPot(int id){
int max=bets[id]+stacks[id];
int ret=0;
for(int i=0;i<playerNb;i++){
ret+=Math.min(max,bets[i]);
}
return ret;
}
public static boolean isNeverFold(int id){
return actionCount[id][ActionUtils.FOLD]==0&&FOWUtils.isUseStatistics();
}
public static int calculateLevel(){
return 1+(Constant.HAND_NB/Constant.HAND_NB_BY_LEVEL);
}
public static int calculateBigBlind(){
return Constant.BIG_BLIND
*(int)Math.pow(Constant.LEVEL_BLIND_MULTIPLIER,calculateLevel()-1);
}
public static boolean hasRoundBet(int id){
int bet=roundBets[id];
return bet>0;
}
public static boolean hasNotBet(int id){
return bets[id]<=bigBlind;
}
public static boolean isAllIn(int id){
return stacks[id]==0;
}
}

