static int index=0;
public static final int FOLD=index++;
public static final int CHECK=index++;
public static final int ALL_IN=index++;
public static final int BET=index++;
public static final int CALL=index++;
public static final int NONE=index++;
public static final int TIMEOUT=index++;
public static int fromString(String action){
if(action.equals("FOLD")){
return FOLD;
}else if(action.equals("CHECK")){
return CHECK;
}else if(action.equals("ALL-IN")){
return ALL_IN;
}else if(action.equals("CALL")){
return CALL;
}else if(action.equals("NONE")){
return NONE;
}else if(action.equals("TIMEOUT")){
return TIMEOUT;
}else{
int amount=Integer.parseInt(action.split(RefereeConstant.WORD_DELIMITER)[1]);
return createBet(amount);
}
}
public static String toString(int action){
int type=getType(action);
if(type==FOLD){
return "FOLD";
}else if(type==CHECK){
return "CHECK";
}else if(type==ALL_IN){
return "ALL_IN";
}else if(type==CALL){
return "CALL";
}else if(type==NONE){
return "NONE";
}else if(type==TIMEOUT){
return "TIMEOUT";
}else{
return "BET "+getAmount(action);
}
}
public static int getType(int action){
return action%10;
}
public static int getAmount(int action){
return action/10;
}
public static int findAction(int action){
Game game=Game.getInstance();
int[] possibleActions=game.possibleActions;
int type=getType(action);
int amount=getAmount(action);
if(type==BET){
if(amount>=GameUtils.stacks[Constant.PLAYER_ID]){
action=ALL_IN;
}
}
if(type==BET){
int bet=findBet(possibleActions);
if(bet!=-1){
int amount2=getAmount(bet);
if(amount2>amount){
return bet;
}else{
return action;
}
}
if(IntListUtils.contains(possibleActions,ALL_IN)){
return ALL_IN;
}
if(IntListUtils.contains(possibleActions,CALL)){
return CALL;
}
if(IntListUtils.contains(possibleActions,CHECK)){
return CHECK;
}

}else if(type==ALL_IN){
if(IntListUtils.contains(possibleActions,ALL_IN)){
return ALL_IN;
}
if(IntListUtils.contains(possibleActions,CALL)){
return CALL;
}
if(IntListUtils.contains(possibleActions,CHECK)){
return CHECK;
}
}else if(type==CALL){
if(IntListUtils.contains(possibleActions,CALL)){
return CALL;
}
if(IntListUtils.contains(possibleActions,CHECK)){
return CHECK;
}
if(IntListUtils.contains(possibleActions,ALL_IN)){
return ALL_IN;
}

int bet=findBet(possibleActions);
if(bet!=-1){
return bet;
}
}else if(type==CHECK){
if(IntListUtils.contains(possibleActions,CHECK)){
return CHECK;
}
return FOLD;
}else{
if(IntListUtils.contains(possibleActions,CHECK)){
return CHECK;
}
}
return FOLD;
}
public static int findBet(int[] actions){
for(int i=0;i<IntListUtils.size(actions);i++){
int action=IntListUtils.get(actions,i);
int type=getType(action);
if(type==BET){
return action;
}
}
return -1;
}
public static int createBet(int amount){
return BET+10*amount;
}
}

