class ChenFormulaUtils{
public static double chenFromRank(int rank){
if(rank<RankUtils.JACK_RANK){
return(rank+2)/2.0;
}
if(rank==RankUtils.JACK_RANK){
return 6;
}
if(rank==RankUtils.QUEEN_RANK){
return 7;
}
if(rank==RankUtils.KING_RANK){
return 8;
}
return 10;
}
public static int calculateHandValue(int[] hand){
double score=0;
int card0=hand[0];
int card1=hand[1];
int rank0=CardUtils.getRank(card0);
int rank1=CardUtils.getRank(card1);
int bestRank=Math.max(rank0,rank1);
score+=chenFromRank(bestRank);
if(rank0==rank1){
score*=2;
if(score<5){
score=5;
}
}
if(CardUtils.getSuit(card0)==CardUtils.getSuit(card1)){
score+=2;
}
int gap=Math.abs(rank0-rank1)-1;
if(gap>=0){
if(gap>0){
if(gap<=2){
score-=gap;
}else if(gap==3){
score-=4;
}else{
score-=5;
}
}
if(gap<=1&&bestRank<RankUtils.QUEEN_RANK){
score++;
}
}
return(int)Math.ceil(score);
}
}

