static double calculatepercent1(){
boolean allIn=callAmount==playerStack;
double bbNb=callAmount/(double)bb;
double p;
if(bbNb>=80){
p=65;
}else if(bbNb>=40){
p=60+(bbNb-40)/10;
}else if(bbNb>=10){
p=55+(bbNb-10)/10;
}else{
p=50;
}
p=(allIn?p+2*(notFolded-2):p);
return p;
}
static double calculatepercent3(){
boolean allIn=callAmount==playerStack;
double bbNb=callAmount/(double)bb;
double p;
if(bbNb>=80){
p=65;
}else if(bbNb>=40){
p=60+(bbNb-40)/10.0;
}else if(bbNb>=10){
p=55+(bbNb-10)/10.0;
}else{
p=50;
}
p=(allIn?p+2*(notFolded-2):p);
p-=2;
return p;
}
static double[] xs ={80,40,10};
static double[] ys ={65,60,55,50 };
static double calculatepercent4(){
boolean allIn=callAmount==playerStack;
double bbNb=callAmount/(double)bb;
double  p=calculateY(bbNb,xs,ys);
p=(allIn?p+2*(notFolded-2):p);
return p-=2;
}
static double calculateY(double x,double[] xs,double[] ys){
boolean order=xs[1]>xs[0];
if(order){
throw new IllegalStateException();
}else{
for(int i=0;i<xs.length;i++){
if(xs[i]<=x){
if(i==0){
return ys[0];
}else{
return calculateY(i,x);
}
}
}
return ys[xs.length -1];
}
}
public static double calculateY(int idx,double x){
return calculateY(xs[idx],xs[idx-1],ys[idx],ys[idx-1],x);
}
public static double calculateY(double x0,double x1,double y0,double y1,double x){
double a=(y0-y1)/(x0-x1);
double b=y0-a*x0;
return a*x+b;
}
