class FindActionClassicUtils extends FindActionUtils{
static Logger logger=Logger.getLogger(FindActionClassicUtils.class);
public static void proceed()throws ActionException{
doFold();
testAction();
doAllInClassic();
testAction();
doCallClassic();
testAction();
}
static void doAllInClassic(){
if(phase==3&&winPercent>=Parameter.ALL_IN_WIN&&notFoldedNeverFold>0){
message="C ALL-IN RIVER "+winPercent;
action=ActionUtils.ALL_IN;
}
}
static void doFold(){
if(useOpponentStats&&opponentPercent>=1.5*winPercent){
message=((int)winPercent)+" OPP "+((int)opponentPercent);
action=ActionUtils.FOLD;
}
}
static void doCallClassic(){
int MIN_PERCENT_FOLD=60;
boolean fold=(!useOpponentStats||winPercent<1.4*opponentPercent)
&&(((winPercent<=MIN_PERCENT_FOLD
&&(!useOpponentStats||winPercent<=opponentPercent))&&callAmount>bb));
fold=fold||winPercent<potOddPercentCall;
fold=foldBigAmount(winPercent)||fold;
logger.error("fold",fold);
if(!fold){
boolean tmp=useOpponentStats;
useOpponentStats=false;
int MIN_PERCENT_RAISE=phase==0?60:(90-phase*10);
int raiseBBNb=(int)Math
.round((winPercent-Math.max(phase==0?50:70,useOpponentStats?opponentPercent:0))
/5);
int bet2=raiseBBNb*bb;
boolean call=bet2<=callAmount||handRaised[Constant.HAND_NB]==phase
||(useOpponentStats&&winPercent<=0.8*opponentPercent)
||winPercent<=MIN_PERCENT_RAISE;
logger.error("call",call);
useOpponentStats=tmp;
if(call){
action=ActionUtils.CALL;
}else{
if(notFoldedNeverFold==0&&useOpponentStats){
if(isKeppOpponentCalling()){
double opponentAvoidFoldBB=FOWUtils.calculateOpponentAvoidFoldBB();
if(opponentAvoidFoldBB>0){
bet2=Math.min(bet2,(int)Math.ceil(opponentAvoidFoldBB-1)*bb);
if(bet2==0&&callAmount<bb){
bet2=callAmount+Parameter.WANT_OPPONENT_TO_CALL_BB*bb;
}
logger.error("KEEP OPPONENT CALLING",bet2,opponentAvoidFoldBB);
message+=" KEEP OPPONENT CALLING";
}
}else if(winPercent>=Parameter.WANT_OPPONENT_TO_CALL_PERCENT2&&phase>0){
bet2=Parameter.WANT_OPPONENT_TO_CALL_BB2*bb;
message+=" ADD ONE BB";
}
}
if(bet2<=callAmount){
bet2=callAmount;
}else if(bet2<=betAmount){
bet2=betAmount;
}
handRaised[Constant.HAND_NB]=phase;
action=ActionUtils.createBet(bet2);
}
}
}
static boolean isKeppOpponentCalling(){
if(phase>0){
return winPercent>=Parameter.WANT_OPPONENT_TO_CALL_PERCENT;
}else{
return winPercent>=Parameter.WANT_OPPONENT_TO_CALL_PERCENT_PREFLOP;
}
}
}

