static void doCallNeverFold(){
int MIN_PERCENT_FOLD=50;
boolean fold =
((winPercent<=MIN_PERCENT_FOLD&&callAmount>bb))||winPercent<potOddPercentCall;
fold=foldBigAmount(winPercent*1.1)||fold;
logger.error("fold",fold);
if(!fold){
if(phase<3){
action=ActionUtils.CALL;
return;
}
int MIN_PERCENT_RAISE=60;
int raiseBBNb=(int)Math.round((winPercent-70)/2);
int bet2=raiseBBNb*bb;
