||winPercent<=MIN_PERCENT_RAISE;
if(call){
action=ActionUtils.CALL;
}else{
if(bet2<=betAmount){
bet2=betAmount;
}
handRaised[Constant.HAND_NB]=phase;
action=ActionUtils.createBet(bet2);
}
}
}
