class FOWUtils{
static final Logger logger=Logger.getLogger(FOWUtils.class);
static int hand=-1;
public static final int NEVER_FOLD=-1;
public static final int DONT_USE=-2;
static FOW[] fows=new FOW[4];
static{
for(int i=0;i<4;i++){
fows[i]=new FOW();;
}
}
public static double calculateOpponentAvoidFoldBB(){
if(!isUseStatistics()){
return 0;
}
double min=Double.MAX_VALUE;
for(int i=0;i<Constant.PLAYER_NB;i++){
if(i!=Constant.PLAYER_ID&&!GameUtils.folded[i]){
if(!GameUtils.isNeverFold(i)){
double winPercent=fows[i].calculatePercent();
int phase=fows[i].phase;
if(phase>=0){
phase=0;
}
double tmp=calculateFoldBBMin(i,phase,winPercent);
logger.error(i,"min fold bb",tmp,"for",winPercent);
min=Math.min(min,tmp);
}
}
}
if(min==Double.MAX_VALUE){
min=0;
}
return min;
}
public static double calculateOpponentForceFoldBB(){
if(!isUseStatistics()){
return 0;
}
double max=0;
for(int i=0;i<Constant.PLAYER_NB;i++){
if(i!=Constant.PLAYER_ID&&!GameUtils.folded[i]){
if(!GameUtils.isNeverFold(i)){
double winPercent=fows[i].calculatePercent();
int phase=fows[i].phase;
if(phase>=0){
phase=0;
}
double tmp=calculateFoldBBMax(i,phase,winPercent);
logger.error(i,"min fold bb",tmp,"for",winPercent);
max=Math.max(max,tmp);
}
}
}
return max;
}
public static double calculateOpponentPercent(){
if(!isUseStatistics()){
return DONT_USE;
}
double max=0;
boolean neverFold=true;
double min=100;
for(int i=0;i<Constant.PLAYER_NB;i++){
if(i!=Constant.PLAYER_ID&&!GameUtils.folded[i]){
if(!GameUtils.isNeverFold(i)){
neverFold=false;
double tmp=fows[i].calculatePercent();
min=Math.min(min,tmp);
max=Math.max(max,tmp);
}
}
}
if(neverFold){
return NEVER_FOLD;
}
if(min==0){
return 0;
}
return max;
}
public static void udate(){
RoundInfo roundInfo=ActionRoundUtils.getLastRoundInfo();
if(roundInfo==null){
return;
}
int handNb=roundInfo.handNb;
if(handNb!=hand){
hand=handNb;
for(int i=0;i<fows.length;i++){
fows[i].reset();
}
}
for(int i=0;i<Constant.PLAYER_NB;i++){
if(i!=Constant.PLAYER_ID&&!GameUtils.folded[i]){
FOW fow=fows[i];
int phase=BoardUtils.calculatePhase(roundInfo.boardCard);
if(GameUtils.folded[i]){
fow.folded=true;
fow.phase=phase;
}else{
if(GameUtils.hasRoundBet(i)){
logger.error("hasRoundBet",i);
double roundBet=GameUtils.roundBets[i]/(double)GameUtils.bigBlind;
boolean allIn=GameUtils.isAllIn(i)&&roundBet>=Parameter.ALL_IN_BB_NB;
fow.winPercent[phase]=findPercent(i,phase,roundBet,allIn);
fow.phase=phase;
}
}
if(GameUtils.isNeverFold(i)){
logger.error(i,"IS A CALLING STATION");
}else{
logger.error(i,fow);
}
}
}
}
static double avg;
static double max;
static int nb;
static double findPercent(int id,int phase,double bet,boolean allIn){
avg=0;
max=0;
nb=0;
boolean useAllPhase=false;
for(int h=1;h<=StatisticUtils.lastHandNb;h++){
HandStatistic statistic=StatisticUtils.statistics[h];
if(statistic!=null){
int bb=statistic.bigBlind;
RoundStatistic round=statistic.rounds[id];
if(round!=null){
for(int p=0;p<=3;p++){
if(phase!=p&&!useAllPhase){
continue;
}
PhaseStatistic phaseS=round.phases[p];
if(phaseS!=null){
int chipAdded=phaseS.chipAdded;
boolean fold=phaseS.fold;
boolean allInTmp=phaseS.allIn;
double phaseBet=chipAdded/(double)bb;
allInTmp=allInTmp&&phaseBet>=Parameter.ALL_IN_BB_NB;
double winPercent=phaseS.winPercent;
if(fold){
}else{
boolean usePercent=(allInTmp&&allIn)||phaseBet>=bet;
max=Math.max(max,winPercent);
if(usePercent){
avg+=winPercent;
nb++;
}
}
}
}
}
}
}
if(nb>0){
avg /= nb;
}else{
avg=Math.min(max*1.1,100);
}
return avg;
}
static double findPercentNoBet(int id,int phase){
boolean useAllPhase=false;
double minPercentNoBet=0;
for(int h=1;h<=StatisticUtils.lastHandNb;h++){
HandStatistic statistic=StatisticUtils.statistics[h];
if(statistic!=null){
RoundStatistic round=statistic.rounds[id];
if(round!=null){
for(int p=0;p<=3;p++){
if(phase!=p&&!useAllPhase){
continue;
}
PhaseStatistic phaseS=round.phases[p];
if(phaseS!=null){
double winPercent=phaseS.winPercent;
if(phaseS.chipAdded>0){
minPercentNoBet=Math.min(minPercentNoBet,winPercent);
}
}
}
}
}
}
return minPercentNoBet;
}
static double calculateFoldBBMin(int id,int phase,double winPercent){
double min=Double.MAX_VALUE;
double maxCall=0;
int nb=0;
boolean useAllPhase=true;
for(int h=1;h<=StatisticUtils.lastHandNb;h++){
HandStatistic statistic=StatisticUtils.statistics[h];
if(statistic!=null){
int bb=statistic.bigBlind;
RoundStatistic round=statistic.rounds[id];
if(round!=null){
for(int p=0;p<=3;p++){
if(phase!=p&&!useAllPhase){
continue;
}
PhaseStatistic phaseS=round.phases[p];
if(phaseS!=null){
boolean fold=phaseS.fold;
if(fold)
logger.error(id,"FOLD------",phaseS.foldChip,phaseS.foldChip/(double)bb,"win",
(int)phaseS.winPercent,(int)winPercent);
if(fold&&phaseS.winPercent>=winPercent*Parameter.DELTA_PERCENT){
double foldBBTmp=phaseS.foldChip/(double)bb;
min=Math.min(foldBBTmp,min);
nb++;
}
if(!fold&&phaseS.winPercent*Parameter.DELTA_PERCENT<=winPercent&&phaseS.chipAdded>0){
double callBBTmp=phaseS.chipAdded/(double)bb;
logger.error(id,"CALL------",phaseS.chipAdded,callBBTmp,"win",
(int)phaseS.winPercent,(int)winPercent);
maxCall=Math.max(maxCall,callBBTmp);
}
}
}
}
}
}
if(min==Double.MAX_VALUE){
min=0;
}
return Math.max(min,maxCall);
}
static double calculateFoldBBMax(int id,int phase,double winPercent){
double min=Double.MAX_VALUE;
double maxCall=0;
double maxFoldPercent=0;
int nb=0;
boolean useAllPhase=true;
for(int h=1;h<=StatisticUtils.lastHandNb;h++){
HandStatistic statistic=StatisticUtils.statistics[h];
if(statistic!=null){
int bb=statistic.bigBlind;
RoundStatistic round=statistic.rounds[id];
if(round!=null){
for(int p=0;p<=3;p++){
if(phase!=p&&!useAllPhase){
continue;
}
PhaseStatistic phaseS=round.phases[p];
if(phaseS!=null){
boolean fold=phaseS.fold;
int PERCENT=80;
if(fold&&phaseS.winPercent>=PERCENT){
maxFoldPercent=phaseS.winPercent;
double foldBBTmp=phaseS.foldChip/(double)bb;
min=Math.min(foldBBTmp,min);
nb++;
}
if(!fold&&phaseS.winPercent<PERCENT&&phaseS.chipAdded>0){
double callBBTmp=phaseS.chipAdded/(double)bb;
maxCall=Math.max(maxCall,callBBTmp);
}
}
}
}
}
}
if(min==Double.MAX_VALUE){
min=0;
}
return Math.max(min,maxCall);
}
public static boolean isNeverFold(double percent){
return isUseStatistics()&&percent==NEVER_FOLD;
}
public static boolean isUseStatistics(double percent){
return isUseStatistics()&&percent>0;
}
public static boolean isOnlyFish(double percent){
return isUseStatistics()&&percent==NEVER_FOLD;
}
public static boolean isUseStatistics(){
return Constant.HAND_NB>Parameter.FOW_HAND;
}
}

