class KevHandPercentUtils{
static Logger logger=Logger.getLogger(KevHandPercentUtils.class);
static int totalScore;
static int simulationNb;
static int maxBoardRank;
public static double calculate(int[] boardKev,int[] handKev,long outs,int nb){
totalScore=0;
simulationNb=0;
maxBoardRank=0;
if(nb==3){
calculateFlop(boardKev,handKev,outs);
}else{
calculateTurn(boardKev,handKev,outs);
}
double score=totalScore/(double)simulationNb;
score+=CactusKevUtils.getRankMax()-maxBoardRank;
return CactusKevUtils.calculateHandScorePercent(score);
}
static void calculateTurn(int[] boardKev,int[] handKev,long outs){
for(int i=0;i<51;i++){
if((outs&(1l << i))==0){
boardKev[4]=CardUtils.toCactusKevRepresentation(i);
int score=CactusKevUtils.calculateBestPossibleKevRank(boardKev,handKev);
totalScore+=score;
simulationNb++;
maxBoardRank=Math.max(CactusKevUtils.calculateRank(boardKev),maxBoardRank);
}
}
}
static void calculateFlop(int[] boardKev,int[] handKev,long outs){
for(int i=0;i<51;i++){
if((outs&(1l << i))==0){
boardKev[3]=CardUtils.toCactusKevRepresentation(i);
for(int j=i+1;j<52;j++){
if((outs&(1l << j))==0){
boardKev[4]=CardUtils.toCactusKevRepresentation(j);
int score=CactusKevUtils.calculateBestPossibleKevRank(boardKev,handKev);
totalScore+=score;
simulationNb++;
maxBoardRank=Math.max(CactusKevUtils.calculateRank(boardKev),maxBoardRank);
}
}
}
}
}
}

class ScoreUtils{
static final Logger logger=Logger.getLogger(ScoreUtils.class);
static final int[] boardKev=new int[5];
