}
public static double calculateScore(int[] board,int[] hand,int nb2){
outs=0;
nb=nb2;
for(int i=0;i<nb;i++){
int card=board[i];
outs |= 1l << card;
boardKev[i]=CardUtils.toCactusKevRepresentation(card);
}
for(int i=0;i<2;i++){
int card=hand[i];
outs |= 1l << card;
handKev[i]=CardUtils.toCactusKevRepresentation(card);
}
double winPercent;
if(nb==5){
winPercent=calculateRiver();
}else if(nb>0){
winPercent=KevHandPercentUtils.calculate(boardKev,handKev,outs,nb);
}else{
winPercent=calculatePreFlop(hand);
}

return winPercent;
}
public static double calculateRiver(){
int playerScore=CactusKevUtils.calculateBestPossibleKevRank(boardKev,handKev);
int winNb=0;
int simulationNb=0;
for(int i=0;i<51;i++){
if((outs&(1l << i))==0){
handOpponent[0]=CardUtils.toCactusKevRepresentation(i);
for(int j=i+1;j<52;j++){
if((outs&(1l << j))==0){
handOpponent[1]=CardUtils.toCactusKevRepresentation(j);
int opponentScore=CactusKevUtils.calculateBestPossibleKevRank(boardKev,handOpponent);
if(opponentScore>=playerScore){
winNb++;
}else{
}
simulationNb++;
}
}
}
}
return 100.0*winNb/simulationNb;
}
public static double calculatePreFlopChen(int[] hand){
int playerScore=ChenFormulaUtils.calculateHandValue(hand);
int winNb=0;
int simulationNb=0;
for(int i=0;i<51;i++){
if((outs&(1l << i))==0){
handOpponent[0]=i;
for(int j=i+1;j<52;j++){
if((outs&(1l << j))==0){
handOpponent[1]=j;
int opponentScore=ChenFormulaUtils.calculateHandValue(handOpponent);
if(opponentScore<=playerScore){
winNb++;
}
simulationNb++;
}
}
}
}
return 100.0*winNb/simulationNb;
}
public static double calculatePreFlop(int[] hand){
int card0=hand[0];
int card1=hand[1];
int rank0=12-CardUtils.getRank(card0);
int rank1=12-CardUtils.getRank(card1);
boolean sameSuit=CardUtils.getSuit(card0)==CardUtils.getSuit(card1);
if((sameSuit&&rank0<rank1)||(!sameSuit&&rank0>rank1)){
int t=rank1;
rank1=rank0;
rank0=t;
}
int index=rank1*13+rank0;
return LookupTableUtils.preflop[index];
}
public static double calculate(int nb){
int simulationNb=0;
int lossNb=0;
loop:
while(true){
tmpOuts=outs;
for(int n=nb;n<5;n++){
int newCard=chooseRandomCard();
boardKev[n]=CardUtils.toCactusKevRepresentation(newCard);
}
long lastOut=tmpOuts;
int playerRank=CactusKevUtils.calculateBestPossibleKevRank(boardKev,handKev);
for(int j=0;j<Parameter.SIMULATION_NB;j++){
if(TimeOutUtils.isStopTime()){
break loop;
}
tmpOuts=lastOut;
handOpponent[0]=CardUtils.toCactusKevRepresentation(chooseRandomCard());
handOpponent[1]=CardUtils.toCactusKevRepresentation(chooseRandomCard());
int opponentRank=CactusKevUtils.calculateBestPossibleKevRank(boardKev,handOpponent);
if(opponentRank<playerRank){
lossNb++;
}
simulationNb++;
}
simulationNb++;
}
logger.error("simulationNb",simulationNb);
if(simulationNb==0){
return 0;
}
return lossNb/(double)simulationNb;
}
static int chooseRandomCard(){
long lastOuts=tmpOuts;
int newCard;
do{
newCard=RandomUtils.chooseRandom(52);
tmpOuts |= 1l << newCard;
}while(tmpOuts==lastOuts);
return newCard;
}
}

