class Binary{
public static final int BINARY_BIT_NB=32;
int[] values;
int length;
public Binary(int length){
this.length=length;
this.values=new int[(int)Math.ceil(length/(double)BINARY_BIT_NB)];
}
public int getLength(){
return length;
}
public int[] getValues(){
return values;
}
public boolean isOne(int i){
int index=i/BINARY_BIT_NB;
return(values[index]&(1 <<(i%BINARY_BIT_NB)))!=0;
}
public void setOne(int i){
int index=i/BINARY_BIT_NB;
values[index] |=(1 <<(i%BINARY_BIT_NB));
}
@Override public String toString(){
StringBuilder sb=new StringBuilder();
for(int i=0;i<length;i++){
if(isOne(i)){
sb.append('1');
}else{
sb.append('0');
}
}
return sb.toString();
}
}

class BinaryStringDecoder{
public static Binary decode(String str){
if(str.isEmpty()){
return new Binary(0);
}
int lastChar=Character.codePointAt(str,str.length()-1);
int cl=EncodeCommon.CHUNK_LENGTH;
int length;
if(lastChar==EncodeCommon.CHUNK_MASK){
length=cl*(str.length()-1);
}else{
length=cl*str.length();
int j=cl-1;
boolean isZero=(lastChar&(1 << j))==0;
while(isZero==((lastChar&(1 << j))==0)){
length--;
j--;
}
}
Binary ret=new Binary(length);
int v=-1;
for(int i=0;i<length;i++){
if(i%cl==0){
v=Character.codePointAt(str,i/cl);
}
boolean isZero=(v&(1 <<(i%cl)))==0;
if(!isZero){
ret.setOne(i);
}
}
return ret;
}
}

class ByteArrayDecoder{
public static byte[] decode(String str){
Binary binary=BinaryStringDecoder.decode(str);
byte[] bytes=ByteArrayDecoder.decode(binary);
return bytes;
}
public static byte[] decode(Binary binary){
int cl=8;

byte[] ret=new byte[binary.getLength()/cl];
int mask=0;
for(int i=0;i<binary.getLength();i++){
if(binary.isOne(i)){
mask=mask|(1 <<(i%cl));
}
if((i+1)%cl==0){
ret[i/8]=(byte)(mask&0xFF);
mask=0;
}
}
return ret;
}
}

class CompactZero{
public static short[] decompactZero(short[] tab){
int l=tab.length;
for(int i=0;i<tab.length;i++){
if(tab[i]<0){
l+=-tab[i]-1;
}
}
short[] ret=new short[l];
int idx=0;
for(int i=0;i<tab.length;i++){
if(tab[i]<0){
for(int j=0;j<-tab[i];j++){
ret[idx++]=0;
}
}else{
ret[idx++]=tab[i];
}
}
return ret;
}
}

class EncodeCommon{
public static final int CHUNK_LENGTH=14;
public static int CHUNK_MASK;
static{
for(int i=0;i<CHUNK_LENGTH;i++){
CHUNK_MASK |= 1 << i;
}
}
}

class IntArrayDecoder{
public static int[] decode(String str){
Binary binary=BinaryStringDecoder.decode(str);
byte[] bytes=ByteArrayDecoder.decode(binary);
return byteArrayToIntArray(bytes);
}
static int[] byteArrayToIntArray(byte[] bytes){

int[] ret=new int[bytes.length/4];
for(int i=0;i<ret.length;i++){
ret[i]=ByteBuffer.wrap(bytes,i*4,4).getInt();
}
return ret;
}
}

class ShortArrayDecoder{
public static short[] decode(String str){
Binary binary=BinaryStringDecoder.decode(str);
byte[] bytes=ByteArrayDecoder.decode(binary);
return byteArrayToArray(bytes);
}
static short[] byteArrayToArray(byte[] bytes){

short[] ret=new short[bytes.length/2];
for(int i=0;i<ret.length;i++){
ret[i]=ByteBuffer.wrap(bytes,i*2,2).getShort();
}
return ret;
}
}

