class GarbageCollectorUtils{
static Logger logger=Logger.getLogger(GarbageCollectorUtils.class);
static int lastRoundGC=0;
static final int BETWEEN_GC_ROUND_NB=20;
static final int AVOID_TIME_OUT_NB=2000000;
static long start;
public static void garbageCollect(int round){
garbageCollect(round,true);
}
public static void avoidTimeOut(){
StringBuilder[] FOR_ALLOC=new StringBuilder[AVOID_TIME_OUT_NB];
for(int i=0;i<AVOID_TIME_OUT_NB;i++){
FOR_ALLOC[i]=new StringBuilder();
}
FOR_ALLOC=null;
garbageCollect(0);
}
public static void garbageCollect(int round,boolean force){
if(force||round-lastRoundGC>20){
System.err.println(
(Timer.getInstance().getTime())
+"GARBAGE COLECTOR START "
+Runtime.getRuntime().freeMemory());
System.gc();
System.err.println(
(Timer.getInstance().getTime())
+"GARBAGE COLECTOR END "
+Runtime.getRuntime().freeMemory());
lastRoundGC=round;
}
}
public static void initFreeMemory(){
start=Runtime.getRuntime().freeMemory();
}
public static void printUsedMemory(){
logger.error("GARBAGE COLECTOR",Runtime.getRuntime().freeMemory()-start);
}
}

class ByteListUtils{
public static void add(byte[] array,byte p){
array[size(array)+1]=p;
array[0]++;
}
public static void remove(byte[] array,byte p){
boolean remove=false;
for(byte i=0;i<size(array);i++){
byte value=get(array,i);
if(remove){
set(array,(byte)(i-1),value);
}else if(value==p){
remove=true;
}
}
if(remove){
array[0]--;
}
}
public static void removeByIndex(byte[] array,int index){
boolean remove=false;
