public static void removeIf(byte[] array,BytePredicate pred){
byte size=size(array);
byte index=1;
byte newSize=size;
for(byte j=0;j<size;j++){
byte p=get(array,j);
if(pred.test(p)){
newSize--;
}else{
array[index++]=p;
}
}
array[0]=newSize;
}
public static byte count(byte[] array,BytePredicate pred){
byte size=size(array);
byte count=0;
for(byte j=0;j<size;j++){
byte p=get(array,j);
if(pred.test(p)){
count++;
}
}
return count;
}
