public static int count;
public static void init(){
time=Parameter.TIME_OUT;
}
public static void stopTimeException()throws TimeOutException{
stopTimeException(time);
}
public static void stopTimeException(int time)throws TimeOutException{
if(isStopTime(time)){
throw EXCEPTION;
}
}
public static boolean isStopTime(int time){
return timer.getTime()>=time;
}
public static boolean isStopTime(){
return isStopTime(time);
}
public static void updateTime(int timeOut){
time=timeOut;
}
public static void updateTimePercent(int percent,int timeout){
updateTime(calculateTimePercent(percent,timeout));
}
public static int calculateTimePercent(int percent,int timeout){
int actual=(int)timer.getTime();
int left=timeout-actual;
return(percent*left/100)+actual;
}
public static void updateTime(){
time=Constant.isFirstRound()?Parameter.TIME_OUT_FIRST:Parameter.TIME_OUT;
}
}

