import re
lineReg = re.compile(r'(?P<name>\w+)\'s (?P<attribute>\w+) is (a(n?) )?"?(?P<value>.*)"?')

numCyborgs = int(input())
cyborgClues = {}
for _ in range(numCyborgs):
    cyborgClues[input()] = {}

numMayhemClues = int(input())
mayhemClues = {}
for _ in range(numMayhemClues):
    line = input()
    r = re.match(lineReg, line)
    mayhemClues[r.group('attribute')] = r.group('value')

numCyborgClues = int(input())
for _ in range(numCyborgClues):
    line = input()
    r = re.match(lineReg, line)
    cyborgClues[r.group('name')][r.group('attribute')] = r.group('value')

for cyborg, clues in list(cyborgClues.items()):
    if any(
            attribute in mayhemClues and mayhemClues[attribute] != value 
            for attribute, value in clues.items()
        ) or (
            'catchphrase' in clues 
            and 'word' in mayhemClues 
            and mayhemClues['word'] not in clues['catchphrase']
        ):
        cyborgClues.pop(cyborg)

if len(cyborgClues) == 0:
   print('MISSING')
elif len(cyborgClues) > 1:
   print('INDETERMINATE')
else:
    print(next(iter(cyborgClues)))
