import sys
import math

class Point:
    def __init__(self,x,y):
        self.x=x
        self.y=y

def squareDist(p1,p2):
    return (p1.x-p2.x)**2+(p1.y-p2.y)**2

def isRightAngled(p1,p2,p3):
    d1 = squareDist(p1,p2)
    d2 = squareDist(p2,p3)
    d3 = squareDist(p3,p1)
    return d1==d2+d3 or d2==d1+d3 or d3==d1+d2


n = int(input())
points = []
for i in range(n):
    x, y = [int(j) for j in input().split()]
    points.append(Point(x,y))


count = 0
for i in range(n-2):
    for j in range(i+1,n-1):
        for k in range(j+1,n):
            if isRightAngled(points[i],points[j],points[k]):
                count+=1

print(count)
