import sys
import math

h, w, n = [int(i) for i in input().split()]
alive = input()
dead = input()



class Cell(object):
    def __init__(self,state,x,y):
        self.x = x
        self.y = y
        self.state = state
    def foresee(self):
        "Foresee my next state"
        n = -self.state # To avoid counting myself as a neighbour

        # Count neighbours
        for i in range(max(0,self.x-1),min(h-1,self.x+1)+1):
            for j in range(max(0,self.y-1),min(w-1,self.y+1)+1):
                if grid[i][j].state==1: n += 1

        if (self.state==1 and alive[n]=='1') or (self.state==0 and dead[n]=='1'):
            self.nextstate = 1 # I will be living
        else:
            self.nextstate = 0 # I will be dead
        return n
    def change(self):
        self.state = self.nextstate




grid = []
for i in range(h):
    inp = input()
    grid.append([Cell('.O'.index(inp[j]),i,j) for j in range(w)])




# Game loop
for t in range(n):

    # Foresee next state of each cell                 # We need two steps (foresee + change) to avoid counting as an
    for i in range(h):                                # alive neighbour a cell which is supposed to be dead at this turn
        for j in range(w):
            grid[i][j].foresee()

    # Change state by nextstate for each cell
    for i in range(h):
        for j in range(w):
            grid[i][j].change()

# Print the grid
for i in range(h):
    for j in range(w):
        print('.O'[grid[i][j].state],end='')
    print('')
