#include <iostream>
using namespace std;

int main()
{
    int H, W, X, Y;
    cin >> H >> W >> X >> Y;
    cin.ignore();

    if (X < 0 || X >= W || Y < 0 || Y >= H) {
        cout << "Invalid coordinate" << endl;
        return 0;
    }

    for (int i = 0; i < H; ++i) {
        string ROW;
        getline(cin, ROW);

        if (i == Y)
            cout << ROW[X] << endl;
    }
}
