#include <iostream>
#include <string>
#include <vector>
#include <algorithm>

using namespace std;

//Struct makes code more readable
struct Coordinate {
    int x, y;
} startPoint, endPoint, currentPoint;

//Allows for more readable comparison in code
bool operator==(const Coordinate &c1, const Coordinate &c2) {
    return c1.x == c2.x && c1.y == c2.y;
}

int main()
{
    int X1, Y1, X2, Y2, N;
    cin >> X1 >> Y1 >> X2 >> Y2 >> N;
    cin.ignore();

    startPoint = {.x = X1, .y = Y1};
    endPoint = {.x = X2, .y = Y2};

    string out = "";
    bool beginCapture = false;

    //Input loop
    for (int i = 0; i < N; ++i) {
        string ROW;
        getline(cin, ROW);

        //Loop over line
        for (int j = 0; j < ROW.length(); ++j) {
            currentPoint = {.x = j, .y = i};

            if (currentPoint == startPoint) {
                beginCapture = true;
            }

            if (beginCapture) {
                out += ROW[j];
            }

            if (currentPoint == endPoint) {
                beginCapture = false;
                goto breakout;
            }
        }
    }
    breakout:
    cout << out << endl;
}

