using System; using System.Linq; using System.IO; using System.Text; using System.Collections; using System.Collections.Generic;	
//https://www.southernliving.com/culture/baby-girl-names
//https://themomfriend.com/handsome-boy-names-with-meanings/
//https://forebears.io/earth/surnames

public class Program
{
	public static void Main()
	{
        var wifeList = new List<string>();
        var suitorList = new List<Fullname>();

        int howMany = int.Parse(Console.ReadLine());
        for (int i = 0 ; i < howMany ; i++)
        {
            string girl = Console.ReadLine();
            wifeList.Add(girl);
        }

        int numOfSuitors = int.Parse(Console.ReadLine());
        for (int i = 0 ; i < numOfSuitors ; i++)
        {
            string bothNames = Console.ReadLine();
            var splitName = bothNames.Split(' ');
            Fullname boy = new Fullname(splitName[0],splitName[1]);
            suitorList.Add(boy);
        }

		foreach (var wif in wifeList)
		{
			foreach (var hus in suitorList)
			{
				hus.CalculateNumberOfCommonLetters(wif);
			}
			var winner = Fullname.FindBiggestNumberOfCommonLetters(suitorList);

            Console.WriteLine(wif + " should marry " + winner.FirstName + " " + winner.LastName);
		}
	}

	public class Fullname
	{
		public string FirstName;
		public string LastName;
		public int NumberOfCommonLetters = 0;

		public Fullname(string fName, string lName)
		{
			FirstName = fName;
			LastName = lName;
		}

		public void CalculateNumberOfCommonLetters(string wif) 
		{
			string WIF = wif.ToUpper();
			string HUS = this.LastName.ToUpper();

            var list1 = WIF.ToCharArray().Distinct().ToList(); 
			string temp1 = string.Join("",list1.ToArray());

			var list2 = HUS.ToCharArray().Distinct().ToList(); 
			string temp2 = string.Join("",list2.ToArray());

			int ct = 0;

			foreach (char ch1 in list1)
			{
				foreach (char ch2 in list2)
				{
					if (ch1 == ch2) 
					{
						ct++;
					}
				}
			}
			NumberOfCommonLetters = ct;
		}

		public static Fullname FindBiggestNumberOfCommonLetters(List<Fullname> list)
		{
			List<Fullname> sortedList = list.OrderByDescending(x => x.NumberOfCommonLetters).ToList();
			return sortedList[0];
		}
	}	
}
