def rot(s: str, n: int) -> str:
    off = ord("A")
    return "".join(chr((ord(c) - off + n) % 26 + off) if c.isupper() else c for c in s)


def main() -> None:
    ciphertext = input()

    shamt = max(range(26), key=lambda n: rot(ciphertext, n).split().count("THE"))
    plaintext = rot(ciphertext, shamt)

    print(plaintext)


if __name__ == "__main__":
    main()
