using System; using System.IO; using System.Text; using System.Collections; using System.Collections.Generic;	

public class Program
{
	public static void Main()
	{
        var EnglishRay = "zero one two three four five six seven eight nine".Split(' ');
		var SpanishRay = "cero, uno, dos, tres, cuatro, cinco, seis, siete, ocho, nueve".Replace(",","").Split(' ');

		int howMany = int.Parse(Console.ReadLine());

		for (int times = 0 ; times < howMany ; times++)
		{
            string thisInput = Console.ReadLine();

            string strNums = thisInput.Substring(1);

            char lang = thisInput.ToUpper()[0];
            var WordRay = EnglishRay;

            if (lang == 'S')
            {
                WordRay = SpanishRay;
            }

            string ans = "";
            foreach (char ch in strNums)
            {
                int i = int.Parse(ch + "");
                ans += WordRay[i] + " ";
            }
            string thisOutput = thisInput + " reads as " + ans.Trim();
            Console.WriteLine(thisOutput);
		}		
	}
}
