def parse(string):
    name, ext = string.split('.')
    index = 0
    while index < len(name) and name[index].isalpha():
        index += 1
        if index == len(name):
            return name, 0, ext
    return name[:index], int(name[index:]), ext

files = [parse(input()) for _ in range(int(input()))]
files.sort()
for letters, numbers, extension in files:
    print(letters, numbers if numbers else '', '.', extension, sep='')

