#include <iostream>
#include <string>
#include <vector>
#include <algorithm>

using namespace std;

/**
 * Auto-generated code below aims at helping you parse
 * the standard input according to the problem statement.
 **/

int main()
{
    int n;

    cin >> n; cin.ignore();
    cerr << n << endl;
    string music;
    std::vector<std::string> notes;
    while (cin >> music) {    
        notes.push_back(music);
        cin.ignore();
        cerr << music << endl;
    }    
    int player=0;
    int no_chair = n;
    cerr << "==> player no chair = " << no_chair << endl;
    // game loop
    // B E C4 B A A C2 G F E B B A
    for(auto note : notes) {
        int note_length = 1;
        size_t found = note.find('2');
        if(found != std::string::npos) {
            note_length = 2;
            cerr << "!! note_length=" << note_length << endl;
        }
        found = note.find('4');
        if(found != std::string::npos) {
            note_length = 4;
            cerr << "!! note_length=" << note_length << endl;
        }
        // found half length on 2 or 4
        size_t found_dot = note.find('.');
        if( found_dot != std::string::npos ) {
            if( note_length == 2 )
            {
                note_length++;
                cerr << "!! note_length=" << note_length << endl;
            }
            if( note_length == 4 )
            {
                note_length+=2;
                cerr << "!! note_length=" << note_length << endl;
            }
        }
        cerr << note << ": ";
        cerr << "no_chair(" << no_chair;
        no_chair += note_length;
        int modulo = no_chair % (n);
        cerr << ") + note_length("<< note_length << ") = " << no_chair << " ";
        player = n - modulo;
        cerr << " % (" << (n) << ") = " << modulo << " => player = " << player << endl;
    }

    cout << player << endl; 
}
