using System; using System.Linq; using System.IO; using System.Text; using System.Collections; using System.Collections.Generic;using System.Text.RegularExpressions;

public class Program
{
	public static void Main()
	{
        List<string> offerList = new List<string>();

        int numOffers = int.Parse(Console.ReadLine());

        for (int whichOffer = 0 ; whichOffer < numOffers; whichOffer++)  
        {
            string offer = Console.ReadLine();
            offerList.Add(offer);
        }

        int gardenHi = int.Parse(Console.ReadLine());

        string STR_GARDEN = "";
        for (int r = 0 ; r < gardenHi; r++) 
        {
            string aRow = Console.ReadLine();
            STR_GARDEN += aRow;	
        }

        STR_GARDEN = StripWeeds(STR_GARDEN);
        var FREQDIC = new Dictionary<string, int>();

        while (STR_GARDEN.Length > 0 )       // populate FREQDIC
        {
            string thisEmoji = STR_GARDEN.Substring(0,2);
            int thisFreq = Regex.Matches(STR_GARDEN, thisEmoji).Count;
            FREQDIC.Add(thisEmoji,thisFreq);
            STR_GARDEN = STR_GARDEN.Replace(thisEmoji,"");
        }

        int totalCost = 0;
        foreach (var item in offerList) 
        {
            var Ray = item.Split(' ');
            int thisPerUnitCost = int.Parse(Ray[0].Substring(1));
            string Plants = Ray[2];
            for (int i = 0 ; i < Plants.Length ; i = i + 2)
            {
                string thisEmoji = Plants.Substring(i,2);
                if (FREQDIC.ContainsKey(thisEmoji))
                {
                    int thisFreq = FREQDIC[thisEmoji];
                    int thisCost = thisFreq * thisPerUnitCost;
                    totalCost += thisCost;
                }
            }
        }

        string sf = String.Format("{0:C0}", totalCost);
        Console.WriteLine(sf);
	}

	public static string StripWeeds(string s)
	{
		for (int i = 32; i < 200 ; i++)
		{
			char ch = (char)i;
			s = s.Replace(ch + "","");
		}
		return s;
	}
}
