#include "stdio.h"
#include "stdlib.h"

int main(void)
{
    char *str;

    str = calloc(sizeof(char), 151);
    scanf("%[^\n]", str);
    for (int i = 0; str[i] != 0; i++)
    {
        if (str[i] >= 'a' && str[i] <= 'z')
        {
            for (int j = 0; j < 13; j++)
            {
                str[i]++;
                if (str[i] > 'z')
                    str[i] = 'a';
            }
        }
        else if (str[i] >= 'A' && str[i] <= 'Z')
        {
            for (int j = 0; j < 13; j++)
            {
                str[i]++;
                if (str[i] > 'Z')
                    str[i] = 'A';
            }
        }
        else if (str[i] >= '0' && str[i] <= '9')
        {
            for (int j = 0; j < 5; j++)
            {
                str[i]++;
                if (str[i] > '9')
                    str[i] = '0';
            }
        }
        printf("%c", str[i]);
    }
    free(str);
    return (0);
}
