using System;
class Solution
{
    static void Main(string[] args)
    {
        int pos = 0;
        string C = Console.ReadLine();

        foreach(string s in C.Split(' '))
        {
            if(s == "Leap"){pos++;}
            else if(s == "Rise"){pos += 2;}
            else if(s == "Sink"){pos--;}
            else if(s == "Fall"){pos -= 2;}
            else{pos = 0;}
        }

        Console.WriteLine(pos);
    }
}
