data.solution:
r=readline
w=+r()
h=+r()
let grid = [...Array(h)].map(x => [...r()])

for (let j = 0; j < h; j++) {
    for (let i = 0; i < w; i++) {
        if (grid[j][i] == '.') {
            let xMin = i !=0 ? i-1 : i
            let xMax = i !=w-1 ? i+2 : i+1
            let yMin = j !=0 ? j-1 : j
            let yMax = j !=h-1 ? j+2 : j+1
            let count = 0
            for (let y = yMin; y < yMax; y++) {
                for (let x = xMin; x < xMax; x++) {
                    if (grid[y][x] == 'x') count++
                }
            }
            grid[j][i] = count || '.'
        }
    }
}

console.log(grid.map(x=>x.map(y=>y=='x'?'.':y).join('')).join('\n'))
