var factors = []
const n = parseInt(readline());
for (let i = 0; i < n; i++) {
    const X = parseInt(readline());
    factors.push(X)
}
var product = 1
for(let i = 2; i <= factors[0]; i++) {
    var valid = true
    for(let j = 0; j < factors.length; j++) {
        if(factors[j]%i>0) {
            valid = false
            break
        }
    }
    if(valid) {
        for(let j = 0; j < factors.length; j++) {
            factors[j] /= i
        }
        product*=i
        i--
    }
}
var biggest = 0
for(let i = 0; i < factors.length; i++) {
    if(biggest<factors[i]) {
        biggest=factors[i]
    }
}
var w = ""
console.log(product)
for(let i = 0; i < factors.length; i++) {
    w=""
    for(let j = 0; j < factors[i]; j++) {
        w+="-"
    }
    console.log(w)
}
