using System;
using System.Linq;
using System.IO;
using System.Text;
using System.Collections;
using System.Collections.Generic;
class Solution
{
    static void Main(string[] args)
    {
        string s = Console.ReadLine().ToUpper().Replace(" ", "");
        string sOutput = "";
        foreach (char c in s)
            if (s.Replace(c.ToString(), "").Length == s.Length - 1)
                sOutput = sOutput + c.ToString();
        Console.WriteLine(sOutput.Length);
    }
}
