import java.util.*;
class Solution {

    public static void main(String args[]) {
        Scanner in = new Scanner(System.in);
        int N = in.nextInt();
        char[] f = new char[9];
        for (int i = 0; i < 9; i++) {
            f[i]=' ';
        }
        boolean p = true;
        for (int i = 0; i < N; i++) {
            int M = in.nextInt();
            f[M] = p?'x':'o';
            p = !p;
        }
        for (int i = 0; i < 3; i++) {
            System.out.println(f[i*3] + "|" + f[1+i*3] + "|" + f[2+i*3]);
        }
    }
}
