using System; using System.Linq; using System.IO; using System.Text; using System.Collections; using System.Collections.Generic; using System.Text.RegularExpressions;


public class Program
{
	public static string bl = " ";//blank
	public static char nl = '\n';// new line

	public static void Main()
	{
        string strMOM = Console.ReadLine();
        string strKID = Console.ReadLine();

        strMOM = strMOM.Replace("Mother ","").Replace("  ",bl).Replace("  ",bl).Replace("  ",bl).Replace("  ",bl);
        strKID = strKID.Replace("Child ","").Replace("  ",bl).Replace("  ",bl).Replace("  ",bl).Replace("  ",bl);

        var MOM_RAY = strMOM.Split(' ');
        var KID_RAY = strKID.Split(' ');

        int numOfFathersAndFakers = int.Parse(Console.ReadLine()); 

        for (int ff = 0 ; ff < numOfFathersAndFakers ; ff++)
        {
            string strDUDE = Console.ReadLine();
            strDUDE = strDUDE.Replace("  ",bl).Replace("  ",bl).Replace("  ",bl).Replace("  ",bl);
            var DUDE_RAY = strDUDE.Split(' ');

            bool allMatches = true;  // until proven otherwise
            for (int i = 1 ; i < DUDE_RAY.Length ; i++)
            {
                string strMMM = MOM_RAY[i];     // M for mother
                string strFFF = DUDE_RAY[i];    // F for father
                string strCCC = KID_RAY[i];     // C for child
                bool bo1 = strMMM.Contains(strCCC[0]) && strFFF.Contains(strCCC[1]);
                bool bo2 = strMMM.Contains(strCCC[1]) && strFFF.Contains(strCCC[0]);
                bool bo3 = bo1 || bo2;
                if (bo3 == false) 
                {
                    allMatches = false;
                    break;
                }
            }
            if (allMatches == true) Console.WriteLine(DUDE_RAY[0].Trim(':') + ", you are the father!");
        }
    }
}
