using System;
using System.Linq;
using System.IO;
using System.Text;
using System.Collections;
using System.Collections.Generic;

class Solution
{
    static void Main(string[] args)
    {
        string G = Console.ReadLine();
        int W = int.Parse(Console.ReadLine());

        bool flag = false;

        if(G == "F"){
            W = (int)(W*1.2);
            flag = true;
        }
        else if(G == "M"){
            W = (int)(W/1.2);
            flag = true;
        }

        if(flag){
            Console.WriteLine(W);
        }
        else{
            Console.WriteLine("UNKNOWN");
        }
    }
}
