using System; using System.Linq; using System.IO; using System.Text; using System.Collections; using System.Collections.Generic; using System.Text.RegularExpressions;

public class Program
{
	public static string bl = " ";    //blank
	public static char nl = '\n';     //new line
	public static char[,] GRID;
	public static int HI;
	public static int WI;

	public static void Main()
	{
        HI = int.Parse(Console.ReadLine());
        List<string> inputList = new List<string>();

        for (int lin = 0 ; lin < HI ; lin++)
        {
            inputList.Add(Console.ReadLine());    
        }

        WI = inputList[0].Length;
        GRID = new char[HI,WI];

        for (int lin = 0 ; lin < HI  ; lin++)
        {
            string aLine = inputList[lin];
            for (int pos = 0 ; pos < WI ; pos++)
            {
                char ch = aLine[pos];
                GRID[lin,pos] = ch;		
            }
        }
        int goodCornCount = 0;
        int badCornCount = 0;

        for (int lin = 0 ; lin < HI  ; lin++)
        {			
            for (int pos = 0 ; pos < WI ; pos++)
            {
                char ch = GRID[lin,pos];
                if (ch == 'c')
                {
                    bool bo = IsThisCornSurrounded(lin, pos);
                    if (bo) 
                    {
                        GRID[lin,pos] = 'C';
                        goodCornCount++;
                    }
                    else
                    {
                        GRID[lin,pos] = '_';
                        badCornCount++;
                    }
                }
            }
        }
        Console.WriteLine(goodCornCount);
        Console.WriteLine(badCornCount);
        int allCorn = goodCornCount + badCornCount;
        double percent = 100.0 * goodCornCount / allCorn;
        percent = Math.Round(percent,0);
        Console.WriteLine(percent + "%");
        PrintGrid();
	}


	public static void PrintGrid()
	{
		for (int lin = 0 ; lin < HI ; lin++)
		{
			for (int pos = 0 ; pos < WI ; pos++)
			{
				char ch = GRID[lin,pos];
				Console.Write(ch);
			}
			Console.WriteLine();
		}
	}

	public static bool IsThisCornSurrounded(int lin, int pos)
	{
		string all = "";
		if (lin - 1 >= 0) // above
		{
			all += GRID[lin-1,pos];
		}

		if (lin + 1 < HI) // below
		{
			all += GRID[lin+1,pos];
		}

		if (pos - 1 >= 0) // toLeft
		{
			all += GRID[lin,pos-1];
		}

		if (pos + 1 < WI) // to right
		{
			all += GRID[lin,pos+1];
		}
		bool bo1 = all.Contains('b');
		bool bo2 = all.Contains('s');
		bool bo3 = bo1 && bo2;
		return bo3;	
	}
}
